import React from "react"
import PropTypes from "prop-types"
import Header from '../components/Header'
import Footer from '../components/Footer'
import { Container, Card, CardTitle, Col, Row, Button} from 'reactstrap'
import { NavLink } from 'react-router-dom'

class MyApartment extends React.Component {

  handleClick = (e) => {
    // a function call being passed from App.js
    this.props.deleteApartment(this.props.apartment.id)

    // update success to true
    //this.setState({ success: true })
    console.log("Insdie of handleclick method now!")
}
  render() {
    const {
      logged_in,
      sign_in_route,
      sign_out_route
    } = this.props
    return (
      <React.Fragment>
        <Header
          logged_in = { logged_in } 
          sign_in_route = { sign_in_route }
          sign_out_route = { sign_out_route }
        />
        <h3 className = "m-3">My Listed Apartments</h3>
        <Container>

        <Row id="cards" xs="1" sm="2" md="3"  className =  'my-auto'>
            { this.props.apartments.map((apartment, index) => {
              return (
              <Col  className = "my-2" key={ index }>
                <Card body >
                  <CardTitle>
                    <h6>{ apartment.bedrooms } bd / { apartment.bathrooms } ba apartment in {apartment.city}, {apartment.state}
                    <br/> ${ apartment.price } </h6>

                    <NavLink to= {`/edit/${apartment.id}`} >
                      <Button>
                        Edit
                      </Button>
                    </NavLink>
                    <br/>
                    <p onClick ={ this.handleClick }>
                     Delete
                    </p>
                  </CardTitle>
                </Card>
              </Col>
              )
            })}

        </Row>
        </Container>
        <Footer />
      </React.Fragment>
    )
  }
}


export default MyApartment
